import React from "react";
import SpeakerIcon from "../icons/speaker";
import PlayIcon from "../icons/play";

const ElementList = props => {
  return (
    <div className="decorated">
      <div className="media">
        <div className="media-left">
          <a href="#" onClick={this.handleChange}>
            {props.data.isPlaying ? <PlayIcon /> : <SpeakerIcon />}
          </a>
        </div>
        &nbsp;&nbsp;
        <div className="media-body">
          <h4 className="media-heading">{props.data.name}</h4>
          {props.data.type === "EXPLICIT" ? (
            <span className="paint-type"> {props.data.type} </span>
          ) : null}
          <span> {props.data.author} </span>
          &nbsp;
          <span>â€¢</span>
          &nbsp;
          <span> {props.data.album} </span>
        </div>
      </div>
    </div>
  );
};

export default ElementList;
